var $=require("jquery"),Widget=require("../widget");describe("AutoRender",function(){it("autoRenderAll",function(e){var t=$('<div data-widget="widget" data-class-name="widget" data-id="test1"></div>');t.appendTo(document.body),Widget.autoRenderAll(function(){var d=Widget.query("#test1");expect(d.get("className")).to.be("widget"),expect(d.element[0]).to.be(t[0]),t.remove(),d.destroy(),e()})}),it("autoRender trigger",function(e){var t=$('<div id="element">element</div>').appendTo(document.body),d=$('<div id="test2" data-widget="widget" data-class-name="widget" data-widget-role="trigger" data-element="#element"></div>');d.appendTo(document.body),Widget.autoRenderAll(function(){var i=Widget.query("#test2");expect(i.get("className")).to.be("widget"),expect(i.get("trigger")[0]).to.be(d[0]),expect(i.element.html()).to.be("element"),d.remove(),t.remove(),i.destroy(),e()})}),it("autoRender template",function(e){var t=$('<div id="tpl"><p>element</p></div>').appendTo(document.body),d=$('<div id="test3" data-widget="widget" data-widget-role="trigger" data-template="#tpl"></div>');d.appendTo(document.body),Widget.autoRenderAll(function(){var i=Widget.query("#test3");expect(i.element.html().toLowerCase()).to.be("<p>element</p>"),d.remove(),t.remove(),i.destroy(),e()})})});