var DAParser=require("../src/daparser"),expect=require("expect.js"),$=require("jquery"),body=document.body;describe("DAParser",function(){it("single data-xx",function(){var e=$('<div data-key="value"></div>').appendTo(body),a=DAParser.parseElement(e);expect(a.key).to.equal("value"),e.remove()}),it("multi data-xx",function(){var e=$('<div data-key="value" data-key2="val2"></div>').appendTo(body),a=DAParser.parseElement(e);expect(a.key).to.equal("value"),expect(a.key2).to.equal("val2"),e.remove()}),it("convert dash-name to camelCasedName",function(){var e=$('<div data-x-y-123a-_B="val" data-x-y="val" data-AbcD-x="val"></div>').appendTo(body),a=DAParser.parseElement(e);expect(a.xY).to.equal("val"),expect(a.abcdX).to.equal("val"),e.remove()}),it("table element",function(){var e=$('<table><tr><td data-x="1"></td></tr></table>').appendTo(body),a=DAParser.parseElement(e.find("td")[0]);expect(a.x).to.equal(1),e.remove()}),it("object",function(){var e=$("<div data-object=\"{'a':'a', 'b':1}\"></div>").appendTo(body),a=DAParser.parseElement(e);expect(a.object).to.eql({a:"a",b:1}),e.remove()})});